mapper.update:
UPDATE sd_base_goods
name=#{name}, code=#{code}, type=#{type}, brand=#{brand}, standard=#{standard}, material=#{material}, buy_price=#{buyPrice}, sale_price=#{salePrice}, goods_desc=#{goodsDesc}, update_time=#{updateTime}, picture=#{picture}
WHERE goods_id=#{goodsId}

mapper.select:
SELECT goods_id, name, code, type, brand, standard, material, buy_price, sale_price, goods_desc, update_time, picture FROM sd_base_goods
and name = #{goodsCondition.name}
and code = #{goodsCondition.code}
and type = #{goodsCondition.type}
and brand = #{goodsCondition.brand}
and standard = #{goodsCondition.standard}
and material = #{goodsCondition.material}
ORDER BY goods_id ASC LIMIT #{rowIndex},#{pageSize};

SELECT
COUNT(goods_id)
FROM
sd_base_goods

	
		and name = #{goodsCondition.name}
	
	
		and code = #{goodsCondition.code}
	
	
		and type = #{goodsCondition.type}
	
	
		and brand = #{goodsCondition.brand}
	
	
		and standard = #{goodsCondition.standard}
	
	
		and material = #{goodsCondition.material}
